    std::cout << "0x2C: (uint32_t) BaseOfCode: 0x" << std::hex << optionalHeader.BaseOfCode << std::endl;
    std::cout << "0x34: (uint32_t) BaseOfData: 0x" << std::hex << optionalHeader.BaseOfData << std::endl;
