    std::cout << "0x46: (uint16_t) MinorOperatingSystemVersion: " << std::hex << optionalHeader.MinorOperatingSystemVersion << std::endl;
    std::cout << "0x48: (uint16_t) MajorImageVersion: " << std::hex << optionalHeader.MajorImageVersion << std::endl;
    std::cout << "0x4A: (uint16_t) MinorImageVersion: " << std::hex << optionalHeader.MinorImageVersion << std::endl;
    std::cout << "0x4C: (uint16_t) MajorSubsystemVersion: " << std::hex << optionalHeader.MajorSubsystemVersion << std::endl;
