    std::cout << "0x9C: (uint32_t) SecurityDirectory VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_SECURITY].VirtualAddress << std::endl;
    std::cout << "0xA0: (uint32_t) SecurityDirectory Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_SECURITY].Size << std::endl;
    std::cout << "0xA4: (uint32_t) BaseRelocationTable VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_BASERELOC].VirtualAddress << std::endl;
    std::cout << "0xA8: (uint32_t) BaseRelocationTable Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_BASERELOC].Size << std::endl;
    std::cout << "0xAC: (uint32_t) DebugDirectory VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_DEBUG].VirtualAddress << std::endl;
    std::cout << "0xB0: (uint32_t) DebugDirectory Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_DEBUG].Size << std::endl;
    std::cout << "0xB4: (uint32_t) ArchitectureSpecificData VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_ARCHITECTURE].VirtualAddress << std::endl;
    std::cout << "0xB8: (uint32_t) ArchitectureSpecificData Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_ARCHITECTURE].Size << std::endl;
    std::cout << "0xBC: (uint32_t) RVAofGP VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_GLOBALPTR].VirtualAddress << std::endl;
    std::cout << "0xC0: (uint32_t) RVAofGP Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_GLOBALPTR].Size << std::endl;
    std::cout << "0xC4: (uint32_t) TLSDirectory VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_TLS].VirtualAddress << std::endl;
    std::cout << "0xC8: (uint32_t) TLSDirectory Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_TLS].Size << std::endl;
    std::cout << "0xCC: (uint32_t) LoadConfigurationDirectory VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_LOAD_CONFIG].VirtualAddress << std::endl;
    std::cout << "0xD0: (uint32_t) LoadConfigurationDirectory Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_LOAD_CONFIG].Size << std::endl;
    std::cout << "0xD4: (uint32_t) BoundImportDirectoryinheaders VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_BOUND_IMPORT].VirtualAddress << std::endl;
    std::cout << "0xD8: (uint32_t) BoundImportDirectoryinheaders Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_BOUND_IMPORT].Size << std::endl;
    std::cout << "0xDC: (uint32_t) ImportAddressTable VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_IAT].VirtualAddress << std::endl;
    std::cout << "0xE0: (uint32_t) ImportAddressTable Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_IAT].Size << std::endl;
    std::cout << "0xE4: (uint32_t) DelayLoadImportDescriptors VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_DELAY_IMPORT].VirtualAddress << std::endl;
    std::cout << "0xE8: (uint32_t) DelayLoadImportDescriptors Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_DELAY_IMPORT].Size << std::endl;
    std::cout << "0xEC: (uint32_t) COMRuntimedescriptor VA: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_COM_DESCRIPTOR].VirtualAddress << std::endl;
    std::cout << "0xF0: (uint32_t) COMRuntimedescriptor Size: 0x" << std::hex << optionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_COM_DESCRIPTOR].Size << std::endl;
